INSERT INTO `products` VALUES ('1083', '1', '1', '1083', 'Vinho Cypress Vineyards Cabernet Sauvignon (750ml)', '', 'O Vinho Cypress Vineyards Cabernet Sauvignon é de cor vermelho violeta, com tonalidades vermelhas. Tem um aroma de chocolate, cereja e baunilha proveniente do carvalho. O sabor é rico, com toques de cereja, groselha e carvalho. O Vinho Cypress Vineyards Cabernet Sauvignon combina perfeitamente com costeletas de cordeiro na brasa, coxa de pato assada com ervas e costelinha de porco ao molho barbecue. Temos grande experiência na entrega do Vinho Cypress Vineyards Cabernet Sauvignon um processo rigoroso de embalagens garantindo a qualidade do produto e chegada em perfeito estado. Entrega em todo o Brasil.', '', '', '', '', null, '', '55', '121.00', '173.00', null, '1100', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
