INSERT INTO `products` VALUES ('1089', '1', '1', '1089', 'Champagne Veuve Clicquot Rich (750ml)', '', 'Origem : França\r\nProdutor : Veuve Clicquot Ponsardin\r\nDescrição : Veuve Clicquot Rich foi criada especialmente para ser combinado com uma seleção de ingredientes que despertam mixologia (técnicas da gastronomia no preparo de bebidas, trazendo para a sua taça xaropes, caldas, espumas e o que mais ditar a criatividade de cada profissional). As notas que compõem a assinatura do champagne são enaltecidas pela presença dos ingredientes : abacaxi, raspas de grapefruit, pepino, aipo,pimentão ou chá, com cubos de gelo.\r\nTeor Alcoólico : 12% Vol.', '', '', '', '', null, '', '50', '418.00', '597.79', null, '1300', '12', '30', '12', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
