INSERT INTO `products` VALUES ('1098', '1', '1', '1098', 'Filé de Anchova fresca (Kg)', '', '<span style=\"font-family: Arial; color: rgb(33, 33, 33);\">\r\nPeixe sempre é uma opção saudável e deliciosa, seja para uma refeição especial, ou para o dia a dia. Pode ser frito, cozido, ensopado, grelhado ou como salada.</span>', '', '', '', '', null, '', '19', '52.80', '75.50', null, '1', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1099', '1', '1', '1099', 'Filé de Atum', '', 'O atum é um dos peixes mais amados no Mundo, graças em parte à popularidade do atum em lata.\r\n\r\nÉ uma ótima fonte de proteína, ferro, magnésio, fósforo, sódio, selênio, niacina, vitamina B6, vitamina B12 e ácido graxo essencial ômega 3.\r\n\r\nAlém de ser uma ótima fonte de proteínas completas, como os peixes de modo geral, o atum faz parte do grupo de peixes ricos em ômega 3, que é um tipo de gordura, conhecido como ácido graxo essencial pois é muito importante para uma boa saúde e o corpo humano não é capaz de produzir, tendo que obtê-lo da alimentação.', '', '', '', '', null, '', '200', '96.80', '138.40', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
