INSERT INTO `products` VALUES ('1102', '1', '1', '1102', 'Filé de Corvina (Fresco) (1kg)', '', 'A corvina é um peixe de água salgada popular no Brasil.Tem carne branca, firme mas não fibrosa, macia e não gordurosa e com espinhos grandes fáceis de tirar.', '', '', '', '', null, '', '100', '52.80', '75.50', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1103', '1', '1', '1103', 'Filet de Merluza (1kg)', '', 'A Merluza é encontrada em águas profundas e de temperaturas baixas. É um peixe bem popular. Pode ser consumido frito, assado, empanado e em diversos pratos.', '', '', '', '', null, '', '100', '26.40', '37.79', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1104', '1', '1', '1104', 'Filé de Olhete', '', 'Olhete é o nome popular de um peixe marinho de escamas, da família Carangidae, que possui corpo alongado, podendo alcançar até 80 cm. de comprimento e pesar 8 quilos. Ocorre em todo o litoral do Brasil, sendo mais comum na região Nordeste e em Santa Catarina. Habita águas relativamente rasas e agitadas, perto\r\nde costões e os jovens podem formar pequenos cardumes. Já os adultos são solitários ou vivem aos pares. Alimenta-se de lulas, crustáceos e pequenos peixes.', '', '', '', '', null, '', '100', '85.80', '122.69', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1105', '1', '1', '1105', 'Filé de Pargo', '', 'O sabor delicado e refinado de sua carne torna o pargo um ótimo peixe para ser saboreado à mesa e faz dele um dos mais utilizados pelos apreciadores de peixes nobres.', '', '', '', '', null, '', '100', '52.80', '75.50', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
