INSERT INTO `products` VALUES ('1107', '1', '1', '1107', 'Filé de Saint Peter', '', 'Carne nobre, famoso por sua leveza e sabor único. Experimente uma pitada de limão e tenha o prazer de uma refeição extremamente saudável e prazerosa.', '', '', '', '', null, '', '100', '44.90', '64.20', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1108', '1', '1', '1108', 'Filé de Sardinha', '', '<span style=\"font-family: Arial; color: rgb(33, 33, 33);\">\r\nCarne de sabor pronunciado com muitas espinhas, para grelhar (à moda portuguesa), fritar ou fazer em escabeche (marinada com temperos).</span>', '', '', '', '', null, '', '97', '17.60', '25.19', null, '1', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1109', '1', '1', '1109', 'Filé de Tainha', '', 'Peixe de sabor pronunciado e carne escura – que fica ainda mais intenso e marcante se preparado de forma inadequada – faz com que muitos passem longe de sua safra. Mas quem gosta não dispensa, de jeito nenhum, uma bela tainha escalada ou frita em postas, com destaque ainda para as ovas – o caviar da nossa terra.', '', '', '', '', null, '', '100', '46.20', '66.09', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1110', '1', '1', '1110', 'Filé de Tambaqui', '', 'Este bonito corte do peixe amazonense possui sabor bastante intenso e diferente. Sua gordura suave e o sabor da carne conferem ao Tambaqui características únicas, que fogem do conhecido sabor de peixes de rio.', '', '', '', '', null, '', '96', '46.20', '66.09', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1111', '1', '1', '1111', 'Filé de Trilha', '', 'A trilha é um peixe delicioso, macio, cozinha num piscar de olhos, e o sabor lembra muito o do camarão, principalmente a pele e partes mais próximas.', '', '', '', '', null, '', '100', '35.20', '50.30', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1112', '1', '1', '1112', 'Vinagre de Maçã La Pastina (250ml)', '', 'O Vinagre de maçã, também designado por vinagre de cidra, é obtido a partir da polpa do fruto que lhe dá o nome, através de um processo de transformação análogo ao da produção de vinagre de vinho. Devido ao sabor forte e acido que caracteriza o vinagre de maça.', '', '', '', '', null, '', '0', '10.37', '14.80', null, '448', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1113', '1', '1', '1113', 'Vinagre de Jerez Tradicional (375ml)', '', '<span style=\"font-stretch: inherit; font-size: 14px; line-height: normal; font-family: effra-n7, effra, sans-serif; vertical-align: baseline; color: rgb(59, 39, 5); text-align: justify; background-color: rgb(255, 255, 255);\">Origem : Espanha Produtor : Fernando de Castilla Ingrediente : Vinagre de vinho, concentrado de mosto de uvas Palomino. Não contém glúten. Este tradicional Vinagre de Jerez foi envelhecido em barris de Jerez para realçar o sabor de uma ampla gama de pratos. Conteúdo : 375 ml Acidez : 7 %</span>', '', '', '', '', null, '', '2', '44.90', '64.20', null, '386', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1114', '1', '1', '1114', 'Vinagre De Vinho Branco com Estragão Beaufor France (500ml)', '', 'Embalagem: 500ml\r\nOrigem: França\r\nDescrição: Vinagre Vinho branco com estragão. Ideal para carnes de sabor mais forte e peixes em geral.\r\nFabricante: Beaufor France', '', '', '', '', null, '', '10', '18.61', '26.60', null, '515', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1115', '1', '1', '1115', 'Crema de Balsâmico Tradicional (250ml)', '', 'O Crema de Balsâmico Tradicional La Pastina consiste em um creme a base de aceto balsâmico único pelo seu sabor delicado agri-doce, veio para atender uma necessidade dos grandes chefs de cozinha que muitas vezes precisam reduzir o vinagre balsâmico de Modena para finalizar seus pratos e dar um toque especial.', '', '', '', '', null, '', '1', '28.12', '40.20', null, '257', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
