INSERT INTO `products` VALUES ('1118', '1', '1', '1118', 'Filé de Cação', '', '<p><span style=\"line-height: 20px; color: rgb(33, 33, 33); background-color: rgb(255, 255, 255); font-family: Arial;\">Sua carne extremamente macia e de sabor leve, é muito versátil, podendo ser utilizada praticamente em qualquer tipo de preparo. Os mais comuns são ensopados como muqueca ou caldeirada. Também pode ser cozido, assado ou empanado. Recomenda-se o preparo do cação ainda congelado, pois preserva melhor o saboroso e nutritivo líquido fisiológico, deixando-o mais suculento.</span><br></p>', '', '', '', '', null, '', '98', '24.20', '34.60', null, '1', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1119', '1', '1', '1119', 'Filé de Garoupa (1kg)', '', 'Você sabia que a garoupa é capaz de engolir um animal marinho inteiro? Facilmente identificado por sua grande boca, o peixe tem carne branca e muito saborosa. Na culinária, fica perfeito quando vai ao forno ou à brasa e é acompanhado por uma bela farofa e um purê de banana cremoso.', '', '', '', '', null, '', '100', '96.80', '138.40', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
