INSERT INTO `products` VALUES ('1122', '1', '1', '1122', 'Ovas de Salmão (250g)', '', '<div>As ovas &nbsp;de salmão são as maiores, e têm visual interessante, de cor alaranjada. Seu interior é oleoso, com sabor suave e pouco persistente. Conhecidas como ikura na culinária japonesa, onde são geralmente servidas em sushis estilo gunkan. Sua principal característica é que explodem na boca e liberam o sabor do salmão! </div><div>Produto congelado.</div><div><br></div>', '', '', '', '', null, '', '71', '118.72', '169.79', null, '250', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
