INSERT INTO `products` VALUES ('1140', '1', '1', '1140', 'Molho de Pimenta Habanero (150ml)', '', 'Molho de Pimenta Habanero é muito picante, e pode chegar a ser até 50 vezes mais forte que a Jalapeño. A Habanero é uma pimenta com um aroma bem característico e particular, sendo assim difícil de confundir com qualquer outra pimenta.', '', '', '', '', null, '', '5', '10.89', '15.59', null, '200', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
