INSERT INTO `products` VALUES ('1143', '1', '1', '1143', 'Mostarda Maille Dijon com Mel (230g)', '', '<span style=\"font-family: Arial; color: rgb(33, 33, 33);\">\r\nO sabor mais adocicado\r\ncombina com aves, presuntos\r\ndefumados e salmão. Dica:\r\nacrescente uma colher com o seu molho de salada em\r\nvinagre balsâmico.</span>', '', '', '', '', null, '', '14', '20.90', '29.89', null, '300', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
