INSERT INTO `products` VALUES ('1155', '1', '1', '1155', 'Vinho Raka Pinotage (750ml)', '', 'Origem : África do Sul\r\nProdutor : Raka\r\nRegião :  Overberg District - Kleinrivier Ward.\r\nUva : Pinotage\r\nDescrição : Apresenta cor rubi intenso. Seu aroma no nariz apresenta um típico charme rústico, com banana, chá, notas tostadas, de especiarias e flores secas. No paladar enche a boca com taninos doces e frescor, recheio frutado e levemente amadeirado e persistente.\r\nTeor Alcoólico : 14% Vol.\r\nHarmonização : Ideal para acompanhar pratos de carne vermelha, massas recheadas e risotos.', '', '', '', '', null, '', '5', '154.00', '220.20', null, '1200', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
