INSERT INTO `products` VALUES ('1163', '1', '1', '1163', 'TEQUILA LA TILICA BRANCO (750ml)', '', 'Tipo ­ 100% Agave\r\nPaís ­ México\r\nVolume ­ 750 ml\r\nComponentes ­ Agave Azul\r\nTeor alcoólico ­ 40%\r\nOrigem da Tequila La Tilica Branco:\r\nA Tequila La Tilica Blanco surgiu como uma homenagem ao estado de Puebla, no México, onde se produz um dos principais artesanatos mexicanos a Tala vera, um tipo de cerâmica branca esmaltada com desenhos coloridos pintados a mão.', '', '', '', '', null, '', '5', '340.56', '487.00', null, '1200', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1164', '1', '1', '1164', 'TEQUILA LA TILICA REPOSADO (750ml)', '', 'ipo ­ 100% Agave Reposado\r\nPaís ­ México\r\nVolume ­ 750 ml\r\nComponentes ­ Agave Azul\r\nTeor alcoólico ­ 40\r\nOrigem da Tequila La Tilica Reposado:\r\nProduzida no estado de Jalisco, México. Onde Jalisco é conhecida por produzir a melhor tequila do mundo.  A Tequila La Tilica Reposado foi criada para especificações muito exatas por uma destilaria pequena, familiar. Os proprietários são conhecidos por produzir algumas das melhores tequilas de Agave.', '', '', '', '', null, '', '5', '340.56', '487.00', null, '1200', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
