INSERT INTO `products` VALUES ('1184', '1', '1', '1184', 'Vodka Ciroc Red Berry (750ml)', '', 'Origem : França\r\nProdutor : Imported by Diageo - Norwalk - C.T.\r\nDescrição : Destilada cinco vezes para garantir a alta qualidade, Ciroc Red Berry apresenta frutas vermelhas maduras e suculentas como framboesa, morango e outros sabores naturais, resultando em uma experiência de sabor que é deliciosamente diferente, no nariz bouquet de bagas com uma pitada de geléia, de final deliciosamente doce.\r\nTeor Alcoólico : 35% Vol.\r\nHarmonização : Ideal para drinks e coquitéis, como \" Ciroc Cosmo Martini \".', '', '', '', '', null, '', '15', '161.57', '231.00', null, '1250', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1185', '1', '1', '1185', 'Vodka Ciroc Peach (750ml)', '', 'Origem : França\r\nProdutor : Imported by Diageo - Norwalk - C.T.\r\nDescrição : Destilada cinco vezes para garantir a alta qualidade, é infundido com uma mistura suculenta de pêssego e outros sabores naturais, com uma pitada de pomar , no nariz um frescor de pêssegos ao sol, numa experiência de sabor que é deliciosamente diferente, de final aveludado.\r\nTeor Alcoólico : 35% Vol.\r\nHarmonização : Ideal para drinks e coquitéis, como clássico \" Peach Bellini \", com um toque vinho espumante.', '', '', '', '', null, '', '9', '161.57', '231.00', null, '1250', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
