INSERT INTO `products` VALUES ('1189', '1', '1', '1189', 'Pistache Iraniano com Casca', '', 'O pistache ou pistáchio (Pistacia vera) pertence à família da Anacardiaceae e género Pistacia. Tanto a árvore quanto o fruto seco verde possuem essa denominação. É uma árvore de folha caduca e pequena (5 a 7 metros de altura, e que tende a inclinar-se) com folhas pinadas dióicas, nativa do sudoeste asiático (Ásia Menor, Irã, Síria e Israel e Palestina), de onde se estendeu o cultivo à região mediterrânica e à Califórnia.', '', '', '', '', null, '', '121', '99.64', '142.49', null, '1', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
