INSERT INTO `products` VALUES ('1193', '1', '1', '1193', 'Jaba/Ponta de Agulha (Kg)', '', '<span style=\"color: rgb(51, 51, 51); font-family: UOLText, arial, verdana, sans-serif; line-height: 20px; background-color: rgb(255, 255, 255);\">Tradicional do Nordeste, é a mais artesanal das carnes curadas. O corte (bovino ou caprino) recebe uma camada de sal fino e é deixado em locais cobertos e bem ventilados para secagem gradual ?não é mais seca ao sol como antigamente. O período de secagem curto resulta numa carne ainda úmida, com menor prazo de validade e que não precisa precisa passar por hidratação ou dessalga na hora de cozinhar.</span>', '', '', '', '', null, '', '18', '20.04', '28.69', null, '1', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
