INSERT INTO `products` VALUES ('1266', '1', '1', '1266', 'Queijo Brie São Vicente peça (aprox 1,1kg)', '', 'De origem francesa, da região de Brie, o queijo tipo Brie é fabricado com leite de vaca. Este queijo apresenta uma casca de mofo branco aveludada comestível, formada por fungos do gênero Penicillium Candidum, e o seu interior é bastante macio. Possui aroma e nota suaves de terra que lembram trufas amanteigadas e o seu sabor é forte e adocicado. Para um sabor mais apurado é indicada uma peça com mais de 30 dias de maturação.', '', '', '', '', null, '', '10', '44.00', '62.90', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
