INSERT INTO `products` VALUES ('1297', '1', '1', '1297', 'Chocolate Amarula Goldkenn', '', 'Os fãs da Amarula ficarão encantados com essa novidade, Feito com o mais puro chocolate suíço ao leite recheados com Amarula.\r\nLicor da África do Sul feita a partir de Marula fruta Chocolate ao leite suíço (37% cacau) preenchido com Amarula® Liquor 100 g\r\nFabricante: Goldkenn S.A.\r\nEmbalagem: 100g\r\nPais de Origem: Suiça\r\nMarca: GOLDKENN', '', '', '', '', null, '', '6', '29.23', '41.80', null, '200', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1298', '1', '1', '1298', 'Cerveja Faxe 1000ml 5% Alc./vol.', '', 'Faxe Premium é uma lager clássica com um sabor suave e característico. A combinação dos melhores do malte, lúpulo e água naturais resultam em uma Cerveja encorpada e ao mesmo tempo, agradavelmente suave.', '', '', '', '', null, '', '0', '20.59', '29.40', null, '1200', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1299', '1', '1', '1299', 'Cerveja Faxe Black 1000ml 4,7% Alc/vol.', '', 'Uma lager de coloração escura, notas de maltes torrados que lembram café e um leve fim de chocolate.Notas de caramelo, lúpulo leve com um toque floral, o aroma segue o sabor com evidente aroma do malte. O seu estilo é classificado como Schwarzbier, o que em alemão quer dizer cerveja preta. Essa designação se refere a cervejas que apesar de apresentar coloração escura, são leves e com alto drinkhability. Uma textura cremosa com um final duradouro e levemente seco. Perfeita para acompanhar pães como o australiano, molhos a base de cream cheese e carnes marinadas em intenso tempero, como peito de frango.', '', '', '', '', null, '', '0', '20.59', '29.40', null, '1250', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
