INSERT INTO `products` VALUES ('1301', '1', '1', '1301', 'Tâmara Fresca (Kg)', '', '<span style=\"color: rgb(51, 51, 51); font-family: Tahoma, Arial, Verdana, sans-serif; font-size: 15px; line-height: 22.5px; background-color: rgb(255, 255, 255);\">A tamara é rica em fibras, Potássio e Cobre, vitaminas, tais como a tiamina, riboflavina, niacina, ácido fólico, vitamina A, K, e ainda possui propriedade altamente energética.&nbsp;</span>', '', '', '', '', null, '', '100', '71.20', '101.80', null, '1', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
