INSERT INTO `products` VALUES ('1326', '1', '1', '1326', 'T-Bone (kg)', '', 'O T-Bone também conhecido bisteca ou chuleta, é um tipo de corte de carne bovina. Ele consiste em um osso em formato de \"T\" com carne dos dois lados. O lado maior é de contra filé, e o lado menor é filé mignon.', '', '', '', '', null, '', '30', '71.28', '101.90', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1327', '1', '1', '1327', 'Joelho de Porco (kg)', '', '<span style=\"font-family: Arial;\"><span style=\"color: rgb(0, 0, 0); font-size: 14px; line-height: normal; background-color: rgb(255, 255, 255);\">O joelho de porco (</span><em style=\"color: rgb(0, 0, 0); font-size: medium; line-height: normal;\"><span style=\"font-size: 14px;\">eisbein</span></em><span style=\"color: rgb(0, 0, 0); font-size: medium; line-height: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 14px;\">) é um prato de</span><span style=\"font-size: 14px;\">&nbsp;</span></span><span style=\"color: rgb(0, 0, 0); font-size: 14px; line-height: normal;\">origem alemã</span><span style=\"color: rgb(0, 0, 0); font-size: medium; line-height: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 14px;\">&nbsp;</span><span style=\"font-size: 14px;\">muito apreciado por terras brasileiras. Tradicionalmente é servido com</span><span style=\"font-size: 14px;\">&nbsp;</span></span><span style=\"font-size: 14px; line-height: normal; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: 0px 0px; background-repeat: initial;\">chucrute</span><span style=\"color: rgb(0, 0, 0); font-size: medium; line-height: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 14px;\">&nbsp;</span><span style=\"font-size: 14px;\">(conserva de repolho) e batata, mas o acompanhamento pode variar de acordo com os gostos.</span></span></span><span style=\"color: rgb(0, 0, 0); font-size: medium; line-height: normal;\"><span style=\"font-family: Arial;\"><br></span><br><br></span>', '', '', '', '', null, '', '24', '8.58', '12.29', null, '1', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
