INSERT INTO `products` VALUES ('1361', '1', '1', '1361', 'Coxão Duro (kg)', '', 'É também conhecido como coxão de fora, chandanca, posta vermelha, perniquim, lagarto plano, lagarto chato, lagarto vermelho, chã de fora e lagarto atravessado.', '', '', '', '', null, '', '30', '27.28', '39.00', null, '1200', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1362', '1', '1', '1362', 'Cupim - (Aproximadamente 3kg)', '', 'Caracterizado pela gordura entremeada da carne, o que proporciona sabor e maciez únicos ao seu corte. O cupim é bastante apreciado pelos brasileiros. Considerado um dos cortes mais saborosos do boi, é muito usado para assados e churrascos, exigindo um longo tempo de cozimento.', '', '', '', '', null, '', '30', '75.24', '107.59', null, '3200', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1363', '1', '1', '1363', 'Entrecote (kg)', '', 'Em francês, a palavra entrecôte indica um corte de carne usado para bifes. O tradicional corresponde à ponta do contra-filé e que leva nomes diferentes em países: noix nos Estados Unidos e bife ancho na França e Argentina. No Brasil, anda sendo feito com a ponto do contra-filé, levado ao forno e servido com molho e batatas fritas, tradicionalmente.', '', '', '', '', null, '', '30', '40.48', '57.89', null, '1200', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1364', '1', '1', '1364', 'Peito Bovino (kg)', '', 'Parte dianteira do boi, constituída de músculos, fibras grossas e compridas, que deixam a carne com uma textura mais firme, ideal para cozidos, caldos, sopas e refogados.', '', '', '', '', null, '', '30', '20.68', '29.59', null, '1200', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
