INSERT INTO `products` VALUES ('1371', '1', '1', '1371', 'Costela de Javali (Aproximadamente 1kg) Congelado', '', 'Bem vermelha, a carne da capivara é saborosa e tem baixos índices de gordura e níveis de ferro 23 vezes mais altos que a carne de boi, além de 24% de proteína bruta a mais que a carne de porco ou boi. Também contém ômega-3, ácido graxo que auxilia a prevenção de doenças cardíacas, reduzindo o \"mau colesterol\".', '', '', '', '', null, '', '30', '50.60', '72.39', null, '1200', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1372', '1', '1', '1372', 'Costela de Queixada (Aproximadamente 1kg) Congelado', '', 'A carne de queixada tem a coloração vermelha escura, pouca gordura, baixo teor de colesterol e sabor bastante acentuado, incomparável a qualquer outra carne.', '', '', '', '', null, '', '30', '118.80', '169.89', null, '1200', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
