INSERT INTO `products` VALUES ('1377', '1', '1', '1377', 'Cordeiro Moído', '', 'Carne moída de Cordeiro. Perfeita para fazer hamburgueres de cordeiro, espaguete com almondegas de cordeiro, empanadas de cordeiro, ravioli recheado com carne de cordeiro ou o famoso prato grego moussaka.', '', '', '', '', null, '', '60', '51.33', '73.39', null, '1200', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1378', '1', '1', '1378', 'Vongole com Casca (1kg)', '', 'Vôngole, amêijoa, cozze. São muitos os nomes dados a um marisco bivalve, típico das águas frias, que pode ser apreciado com qualidade em algumas regiões do mundo, principalmente na faixa litorânea da Itália e no Alentejo, em Portugal. Como bom fruto do mar, não é preciso muito esforço para que ele garanta um sabor especial ao prato. Vão muito bem em entradas, com molhos leves; e em pratos principais, com massas ou mesmo combinados com outras proteínas  talvez, a composição mais surpreendente.', '', '', '', '', null, '', '100', '13.20', '18.89', null, '1200', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1379', '1', '1', '1379', 'Medalhão de Salmão Congelado (600g)', '', 'Salmão é o nome vulgar de várias espécies de peixes da família Salmonidae, que também inclui as trutas, típicos das águas frias do norte da Eurásia e da América. Várias espécies são criadas em aquacultura, especialmente a espécie Salmo salar.', '', '', '', '', null, '', '20', '51.33', '73.39', null, '1200', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1380', '1', '1', '1380', 'Vieira Grande Limpa Congelada sem Banho', '', 'Na cozinha italiana, ela é conhecida como capasanta, conchiglia di S. Giacomo ou pettine, e é muito usada em saladas, risotos, com massas, salteadas, em entradas e até in natura, como as ostras. Pelo sabor suave, as vieiras combinam muito bem com temperos mais simples, como um pouco de vinho branco, alho e salsa.', '', '', '', '', null, '', '59', '277.99', '397.50', null, '1200', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1381', '1', '1', '1381', 'Carne de Siri (Congelada)', '', 'Tão rica quanto a do caranguejo, a carne de siri tem baixas calorias e é de fácil digestão, podendo ser utilizada em saladas, musses, patês, gratinados ou ensopados.', '', '', '', '', null, '', '97', '55.00', '78.69', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
