INSERT INTO `products` VALUES ('1427', '1', '1', '1427', 'Café Carrua Lata (500 grs. Moído)', '', 'Na África, o café selvagem nascia no meio da mata, no centro de kaffa, interior da Etiópia. Local de onde nasceu o vocábulo \"Café\" herdado da palavra árabe Qahwah ou Qahwah, que se pronuncia \"Carruá\" nome desta seleção de grãos torrados de forma artesanal produzidos das serras do Sul de Minas e Mogiana.', '', '', '', '', null, '', '998', '30.02', '42.90', null, '600', '15', '15', '15', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
