INSERT INTO `products` VALUES ('1475', '1', '1', '1475', 'Chouriço Fatiado (100 Grs)', '', 'O chouriço extra fatiado tradição familiar  tem uma alta percentagem de proteínas de grande qualidade já que os seus componentes proporcionam uns 45% de ácido oléico que não são fabricados pelo nosso organismo, e que é indispensável para a nossa saúde. Apresenta um aroma fino, cor brilhante e um sabor suave com um toque especial a colorau. Produto perfeito para cozidos o para fazer a la sidra pelo seu equilibrado conteúdo em gordura e grande sabor.', '', '', '', '', null, '', '12', '10.00', '18.19', null, '100', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
INSERT INTO `products` VALUES ('1476', '1', '1', '1476', 'Presunto Serrano Bodega Fatiado ( 70 grs )', '', 'Presunto de porco branco (parte traseira), sal e açúcar com tempo de cura (secagem) de 18 meses.\r\nEm fatias extra finas, fáceis e práticos com qualidade garantida.\r\nNovidade no mercado, este presunto não depende de refrigeração, podendo assim estar fora da geladeira em qualquer lugar da cozinha!', '', '', '', '', null, '', '102', '10.00', '17.09', null, '70', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
