INSERT INTO `products` VALUES ('1478', '1', '1', '1478', 'Morcela (Kilo)', '', 'Morcela : É muito boa cozida com batatas e verduras ou, depois de escaldada, para a \'massa\' aquecer, deixar fritar numa panela grossa, com azeite.A pele deve ficar bem torradinha e serve-se cortada em rodelas com batatas, verduras cozidas e, como eu gosto, regadas com um pouco do azeite em que fritam.Podem também ser assadas no forno.', '', '', '', '', null, '', '53', '33.00', '47.30', null, '1000', '0', '0', '0', '1', '2016-08-17 16:11:59', '2016-08-17 16:11:59', '0');
